mapper.select:
select role_id, role_auth.role_name, `add`, del, search, edit
from role_auth,
     role_info
where role_auth.role_name = role_info.role_name
  and role_auth.role_name like concat('%', #{role_name}, '%');

select role_id, role_auth.role_name, `add`, del, search, edit
from role_auth,
role_info
where role_auth.role_name = role_info.role_name
and role_auth.role_name =#{role_name};
