mapper.update:
create table ${dbName}.${tbName}(

    ${item.colName} ${item.datatype}(${item.len}) unique
    not null;

file varchar(64),
record_ID int auto_increment,

    primary key (

        ${pk}

    ),

index (record_ID),
index (file)
);
update db_info set tables=tables+1 where db_name=#{dbName};
insert into tb_info(db, tb_name) select db_id,#{tbName} from db_info where db_name=#{dbName};
insert into column_info

    select tb_id,
    #{item.colName},#{item.datatype},#{item.len},#{item.place},#{item.PK},#{item.not_null},#{item.uni}
    from tb_info where db=(select db_id from db_info where db_name=#{dbName}) and tb_name=#{tbName}

drop table ${dbName}.${tbName};
update db_info
set tables=tables - 1
where db_name = #{dbName};
delete
from tb_info
where tb_name = #{tbName}
  and db = (select db_id from db_info where db_name = #{dbName});
